import { unpackData } from '../utils.js'

/**
 * Addons API Client
 */
export default class Addons {
  /**
   * Create a new Client for the Addons API
   * @param {Client} client - Scalingo API Client
   */
  constructor(client) {
    this._client = client
  }

  /**
   * Get list of addons of an application
   * @see https://developers.scalingo.com/addons#list-application-addons
   * @param {String} appId ID of the app to get the addons from
   * @return {Promise<Addon[] | APIError>}
   */
  for(appId) {
    return unpackData(
      this._client.apiClient().get(`/apps/${appId}/addons`),
      'addons',
    )
  }

  /**
   * Add an addon to an application
   * @see https://developers.scalingo.com/addons#provision-an-addon
   * @param {String} appId ID of the app
   * @param {String} planId ID of the plan
   * @param {String} addonProviderId ID of the addon provider
   * @return {Promise<Addon | APIError>}
   */
  create(appId, planId, addonProviderId) {
    return unpackData(
      this._client.apiClient().post(`/apps/${appId}/addons`, {
        addon: { plan_id: planId, addon_provider_id: addonProviderId },
      }),
      'addon',
    )
  }

  /**
   * List addon categories
   * @see https://developers.scalingo.com/addon_providers#list-addon-categories
   * @return {Promise<Category[] | APIError>}
   */
  listCategories() {
    return unpackData(
      this._client.unauthenticatedClient().get('/addon_categories'),
      'addon_categories',
    )
  }

  /**
   * List addon providers
   * @see https://developers.scalingo.com/addon_providers#list-addon-providers
   * @param {?String} categoryId ID of the addon category
   * @return {Promise<AddonProvider[] | APIError>}
   */
  listProviders(categoryId) {
    if (categoryId) {
      return unpackData(
        this._client
          .unauthenticatedClient()
          .get(`/addon_providers?category_id=${categoryId}`),
        'addon_providers',
      )
    } else {
      return unpackData(
        this._client.unauthenticatedClient().get(`/addon_providers`),
        'addon_providers',
      )
    }
  }

  /**
   * Upgrade an addon
   * @see https://developers.scalingo.com/addons#upgrade-an-addon
   * @param {String} appId ID of the current application
   * @param {Object} addon Contain the ID of the plan of your choice
   * @param {String} addonId ID of the current addon
   * @return {Promise<AddonUpgrade | APIError>}
   */
  update(appId, addonId, addon) {
    return unpackData(
      this._client
        .apiClient()
        .patch(`/apps/${appId}/addons/${addonId}`, { addon: addon }),
      'addon',
    )
  }

  /**
   * Remove an addon
   * @see https://developers.scalingo.com/addons#remove-an-addon
   * @param {String} appId ID of the current application
   * @param {String} addonId ID of the addon
   * @return {Promise<? | APIError>}
   */
  destroy(appId, addonId) {
    return unpackData(
      this._client.apiClient().delete(`/apps/${appId}/addons/${addonId}`),
    )
  }

  /**
   * Get the sso of an addon
   * @param {String} appId The ID of the current application
   * @param {String} addonId The ID of the addon
   * @return {Promise<AddonSso | APIError>}
   */
  sso(appId, addonId) {
    return unpackData(
      this._client.apiClient().get(`/apps/${appId}/addons/${addonId}/sso`),
      'addon',
    )
  }

  /**
   * Get a specific addon
   * @param {String} appId The ID of the current application
   * @param {String} addonId The ID of the addon to get
   * @return {Promise<Addon | APIError>}
   */
  getAddon(appId, addonId) {
    return unpackData(
      this._client.apiClient().get(`/apps/${appId}/addons/${addonId}`),
      'addon',
    )
  }
}

/**
 * @typedef {Object} AddonSso
 * @property {String} id The id of the addon
 * @property {String} appId The id of the current App
 * @property {String} resourceId The id of the resource
 * @property {Plan} plan Embedded reference to Plan resource
 * @property {AddonProvider} addon_provider Embedded reference to AddonProvider resource
 * @property {Date} provisioned_at When the addon has been created
 * @property {Date} deprovisioned_at When the addon has been deleted/upgraded
 * @property {String} status The current status of the addon
 * @property {String} ssoUrl The sso url of the addon
 */

/**
 * @typedef {Object} Addon
 * @property {String} id Unique ID identifying the addon
 * @property {Date} provisioned_at When the addon has been created
 * @property {Date} deprovisioned_at When the addon has been deleted/upgraded
 * @property {String} resource_id Resource reference
 * @property {Plan} plan Embedded reference to Plan resource
 * @property {AddonProvider} addon_provider Embedded reference to AddonProvider resource
 */

/**
 * @typedef {Object} Plan
 * @property {String} id Unique ID of the plan
 * @property {String} name Name of the plan (internal reference)
 * @property {String} display_name User friendly name
 * @property {number<float>} price The price of the plan in euros
 * @property {String} description Description of the plan
 */

/**
 * @typedef {Object} AddonProvider
 * @property {String} id Unique ID of the addon provider
 * @property {String} name Name of the addon provider
 * @property {String} logo_url The url of the logo
 * @property {String} short_description Short description of the addon provider
 * @property {String} description Complete description of the addon provider
 * @property {Category} category Embedded category object
 * @property {String} provider_name Name of the company offering this addon
 * @property {String} provider_url URL of the company offering this addon
 * @property {Plan[]} plans Embedded array of plans for this addon
 */

/**
 * @typedef {Object} Category
 * @property {String} id ID of the category
 * @property {String} description Description of the category
 * @property {String} name Name of the category
 */

/**
 * @typedef {Object} AddonUpgrade
 * @property {String[]} vars Array of variables
 * @property {String} message Custom message from the addon provider
 */

import { unpackData } from '../utils.js'
import Listener from '../Deployments/listener.js'

/**
 * Apps API Client
 */
export default class Apps {
  /**
   * Create a new Client for the App API
   * @param {Client} client - Scalingo API Client
   */
  constructor(client) {
    this._client = client
  }

  /**
   * Fetch a specific app
   * @see https://developers.scalingo.com/apps#get-a-precise-application
   * @param {String} id - ID of the application
   * @return {Promise<App, APIError>} Promise that when resolved returns an App. See: https://developers.scalingo.com/apps#application-attributes
   */
  find(id) {
    return unpackData(this._client.apiClient().get(`/apps/${id}`), 'app')
  }

  /**
   * Get all your applications and the one your are collaborator for
   * @see https://developers.scalingo.com/apps#list-your-applications
   * @return {Promise<App[], APIError>} Promise that when resolved returns an App array. See: https://developers.scalingo.com/apps#application-attributes
   */
  all() {
    return unpackData(this._client.apiClient().get('/apps'), 'apps')
  }

  /**
   * Create a new application
   * @see https://developers.scalingo.com/apps#create-an-application
   * @param {String} name - Name of the application
   * @param {AppCreateOpts} opts - Optional additional information
   * @return {Promise<App, APIError>} Promise that when resolved returns the App created.
   */
  create(name, opts) {
    const body = {
      name: name,
    }

    const headers = {}

    if (opts) {
      body['git_source'] = opts['git_source']
      body['parent_id'] = opts['parent_id']
      body['stack_id'] = opts['stack_id']
      if (opts['dry_run']) {
        headers['X-Dry-Run'] = 'true'
      }
    }
    return unpackData(
      this._client.apiClient().post('/apps', { app: body }, { headers }),
      'app',
    )
  }

  /**
   * Open a listener on this app deployment events
   * @see https://developers.scalingo.com/deployments#get-real-time-output-of-a-live-deployment
   * @param {String} id ID of the application
   * @return {Promise<Listener, APIError>} Promise that when resolved returns a Listener for this application.
   */
  async deploymentListener(id) {
    const app = await this.find(id)
    return new Listener(this._client, app.links.deployments_stream)
  }

  /**
   * Get an authenticated URL for the application logs
   * @see https://developers.scalingo.com/apps#access-to-the-application-logs
   * @param {String} id ID of the application
   * @return {Promise<String, APIError>} Promise that when resolved returns a pre-signed URL to access application logs.
   */
  logsURL(id) {
    return unpackData(
      this._client.apiClient().get(`/apps/${id}/logs`),
      'logs_url',
    )
  }

  /**
   * Destroy the given application.
   * @see https://developers.scalingo.com/apps.html#delete-an-application
   * @param {String} appID ID of the application
   * @param {String} currentName Current name of the application. Used as validation.
   * @return {Promise<null, APIError>} Promise that resolves when the app is deleted.
   */
  destroy(appID, currentName) {
    return unpackData(
      this._client
        .apiClient()
        .delete(`/apps/${appID}`, { params: { current_name: currentName } }),
    )
  }

  /**
   * Rename the application.
   * @see https://developers.scalingo.com/apps.html#rename-an-application
   * @param {String} appID ID of the application
   * @param {String} currentName Current name of the application. Used as validation.
   * @param {String} newName New name of the application.
   * @return {Promise<App, APIError>} Promise that when resolved returns the App renamed.
   */
  rename(appID, currentName, newName) {
    return unpackData(
      this._client.apiClient().post(`/apps/${appID}/rename`, {
        current_name: currentName,
        new_name: newName,
      }),
      'app',
    )
  }

  /**
   * Transfer the ownership of the application to a new account.
   * @see https://developers.scalingo.com/apps.html#transfer-ownership-of-an-application
   * @param {String} appID ID of the application
   * @param {String} currentName Current name of the application. Used as validation.
   * @param {String} ownerEmail Email address of the new owner.
   * @return {Promise<App, APIError>} Promise that when resolved returns the App transferred.
   */
  transfer(appID, currentName, ownerEmail) {
    return unpackData(
      this._client.apiClient().patch(
        `/apps/${appID}`,
        {
          app: {
            owner: ownerEmail,
          },
        },
        { params: { current_name: currentName } },
      ),
      'app',
    )
  }

  /**
   * Update application settings: force HTTPS, sticky session, stack or router
   * logs.
   * @see https://developers.scalingo.com/apps#update-application-settings
   * @param {String} appID ID of the application
   * @param {AppUpdateOpts} appSettings - Settings to modify
   * @return {Promise<App, APIError>} Promise that when resolved returns the updated App.
   */
  update(appID, appSettings) {
    return unpackData(
      this._client.apiClient().patch(`/apps/${appID}`, { app: appSettings }),
      'app',
    )
  }
}

/**
 * @typedef {Object} App
 * @see https://developers.scalingo.com/apps
 * @property {String} id unique ID
 * @property {String} name  name of the application, can substitute the ID
 * @property {Date} created_at creation date of the application
 * @property {Date} updated_at last time the application has been updated
 * @property {String} git_url URL to the GIT remote to access your application
 * @property {Object} owner information about the owner of the application
 * @property {String} url platform allocated URL to access to your app
 * @property {AppLinks} links object of related link like deployments_stream
 * @property {Boolean} force_https activation of force HTTPS
 * @property {Boolean} sticky_session activation of sticky session
 * @property {Boolean} router_logs activation of the router logs in your app logs
 * @property {Date} last_deployed_at date of the last deployment attempt
 * @property {String} last_deployed_by user who attempted the last deployment
 * @property {String} last_deployment_id id of the last successful deployment
 */

/**
 * @typedef {Object} AppLinks
 * @property {String} deployments_stream Websocket used to listen for deployment events on this app.
 */

/**
 * @typedef {Object} AppCreateOpts
 * @see https://developers.scalingo.com/apps#create-an-application
 * @property {?String} parent_id ID of the parent app (used to create child apps)
 * @property {?String} stack_id ID of the stack the application should use
 * @property {?Boolean} dry_run If set to true, the API will run the validations but wont create the app
 */

/**
 * @typedef {Object} AppUpdateOpts
 * @see https://developers.scalingo.com/apps#update-application-settings
 * @property {?Boolean} force_https Enable or disable force HTTPS on the application
 * @property {?Boolean} sticky_session Enable or disable sticky session on the application
 * @property {?Boolean} router_logs Enable or disable the router logs on the application
 * @property {?String}  stack_id New stack ID
 */

import { unpackData } from '../utils'

/**
 * Billing API Client
 */
export default class Billing {
  /**
   * Create new Client for the Billing API
   * @params {Client} client - Scalingo API Client
   */
  constructor(client) {
    this._client = client
  }

  /**
   * Return the billing profile of current user
   * @see https://developers.scalingo.com/billing#billing-profile
   * @return {Promise<BillingProfile | APIError>}
   */
  profile() {
    return unpackData(
      this._client.billingApiClient().get('/profile'),
      'profile',
    )
  }

  /**
   * Create the billing profile of current user. Returns an error if already existing
   * @see https://developers.scalingo.com/billing#billing-profile
   * @param {BillingProfile} profile The billing profile to create
   * @return {Promise<BillingProfile | APIError>}
   */
  createProfile(profile) {
    return unpackData(
      this._client.billingApiClient().post('/profiles', { profile }),
      'profile',
    )
  }

  /**
   * Update the billing profile of current user. Returns an error if not already existing
   * @param {String} id The if of the billing profile to update
   * @param {BillingProfile} profile The attributes to update
   * @return {Promise<BillingProfile | APIError>}
   */
  updateProfile(id, profile) {
    return unpackData(
      this._client.billingApiClient().put(`/profiles/${id}`, { profile }),
      'profile',
    )
  }
}

/**
 * @typedef {Object} BillingProfile
 * @property {String} id
 * @property {String} name
 * @property {?String} email
 * @property {?String} credit
 * @property {String} address_line1
 * @property {?String} address_line2
 * @property {String} address_zip
 * @property {String} address_city
 * @property {?String} address_state
 * @property {String} address_country
 * @property {?String} vat_number
 * @property {?String} company
 * @property {?String} payment_method
 * @property {?StripePaymentMethod} stripe_payment_method
 * @property {?PaypalPaymentMethod} paypal_payment_method
 */

/**
 * @typedef {Object} StripePaymentMethod
 * @property {String} id
 * @property {?String} default_card_last4
 * @property {?String} default_card_exp
 * @property {?String} default_card_brand
 * @property {?String} default_card_name
 * @property {?String} sepa_bank_code
 * @property {?String} sepa_country
 * @property {?String} sepa_last4
 * @property {?String} sepa_mandate_reference
 * @property {?String} sepa_mandate_url
 */

/**
 * @typedef {Object} PaypalPaymentMethod
 * @property {String} id
 */

import { unpackData } from '../utils'

/**
 * Collaborators API Client
 */
export default class Collaborators {
  /**
   * Create new Client for the Collaborators API
   * @params {Client} client - Scalingo API Client
   */
  constructor(client) {
    this._client = client
  }

  /**
   * List all collaborators of an application
   * @see https://developers.scalingo.com/collaborators#list-collaborators-of-an-app
   * @params {String} appId ID of the app to get collaborators list
   * @return {Promise<Collaborator[] | APIError>}
   */
  for(appId) {
    return unpackData(
      this._client.apiClient().get(`/apps/${appId}/collaborators`),
      'collaborators',
    )
  }

  /**
   * Remove a collaborator
   * @see https://developers.scalingo.com/collaborators#delete-a-collaborator
   * @params {String} appId ID of the application
   * @params {String} collaboratorId ID of the collaborator to remove
   * @return {Promise<?APIError>}
   */
  destroy(appId, collaboratorId) {
    return unpackData(
      this._client
        .apiClient()
        .delete(`/apps/${appId}/collaborators/${collaboratorId}`),
    )
  }

  /**
   * Invite collaborators to an application
   * @see https://developers.scalingo.com/collaborators#invite-collaborator-to-work-on-an-app
   * @params {String} appId Id of the application
   * @params {String} email Email of the collaborator to invite
   * @return {Promise<CollaboratorInvitation | APIError>}
   */
  invite(appId, email) {
    return unpackData(
      this._client.apiClient().post(`/apps/${appId}/collaborators`, {
        collaborator: { email: email },
      }),
      'collaborator',
    )
  }

  /**
   * Accept invitation to an application
   * @see https://developers.scalingo.com/collaborators#accept-an-invitation-to-collaborate
   * @params {String} token Token of the invitation returned when adding a collaborator
   * @return {App | APIError}
   */
  inviteAccept(token) {
    return unpackData(
      this._client
        .apiClient()
        .get('/apps/collaboration', { params: { token: token } }),
    )
  }
}

/**
 * @typedef {Object} CollaboratorInvitation
 * @property {String} id Id of the collaborator
 * @property {String} email Email of the collaborator to invite
 * @property {String} username Username of the person to invite
 * @property {String} status Status of the invitation
 * @property {String} invitation_link Link of for the invitation
 */

/**
 * @typedef {Object} Collaborator
 * @property {String} id Id of the collaborator
 * @property {String} email Email of the collaborator to invite
 * @property {String} username Username of the person to invite
 * @property {String} status Status of the invitation
 */

import { unpackData } from '../utils.js'
import { Operation } from '../Operations/utils'

/**
 * Containers API Client
 */
export default class Containers {
  /**
   * Create a new Client for the Containers API
   * @param {Client} client - Scalingo API Client
   */
  constructor(client) {
    this._client = client
  }

  /**
   * Get container formation for an app
   * @see https://developers.scalingo.com/apps#get-containers-list
   * @param {String} appId ID of the app to get the formation from
   * @return {Promise<Container[] | APIError>}
   */
  for(appId) {
    return unpackData(
      this._client.apiClient().get(`/apps/${appId}/containers`),
      'containers',
    )
  }

  /**
   * Scale an application
   * @see https://developers.scalingo.com/apps#scale-an-application
   * @param {String} appId ID of the app to scale
   * @param {Container[]} formation Formation to apply
   * @return {Promise<ContainersOperation | APIError>} final formation
   */
  async scale(appId, formation) {
    const result = await unpackData(
      this._client
        .apiClient()
        .post(`/apps/${appId}/scale`, { containers: formation }),
      'containers',
      { hasOperation: true },
    )
    const operation = new Operation(this._client, result.operation)
    await operation.refresh()
    return { formation: result.data, operation: operation }
  }

  /**
   * List the every sizes of the containers
   * @see https://developers.scalingo.com/container-sizes#list-the-container-sizes-available
   * @return {Promise<ContainerSize[] | APIError>} attributes of each container
   */
  availableSizes() {
    return unpackData(
      this._client.apiClient().get('/features/container_sizes'),
      'container_sizes',
    )
  }

  /**
   * Restart containers
   * @see https://developers.scalingo.com/apps#restart-an-application
   * @param {String} appId ID of the app to scale
   * @param {?String[]} scope Array of containers name to restart
   * @return {Promise<Operation | APIError>} final formation
   */
  async restart(appId, scope) {
    const result = await unpackData(
      this._client.apiClient().post(`/apps/${appId}/restart`, { scope: scope }),
      undefined,
      { hasOperation: true },
    )
    const operation = new Operation(this._client, result.operation)
    await operation.refresh()
    return operation
  }
}

/**
 * @typedef {Object} ContainersOperation
 * @property {Container[]} formation Response of the API call
 * @property {Operation} operation Operation information
 */

/**
 * @typedef {Object} Container
 * @property {String} name Type of container (web, worker, etc.)
 * @property {number} amount Amount of containers of the given type
 * @property {String} size Size of the containers of this type (S/M/XL/..)
 */

/**
 * @typedef {Object} ContainerSize
 * @property {String} id Unique universal identifier
 * @property {String} name Name of the size
 * @property {String} human_name Display name of the type
 * @property {Number} ordinal Sorting index to display a list of sizes
 * @property {Number} hourly_price Price per hour of this container size in cents
 * @property {Number} thirtydays_price Price for 30 days in cents
 * @property {Number} memory RAM allocated to the containers in bytes
 * @property {String} human_cpu Human representation of the CPU priority
 */

import WebSocket from 'isomorphic-ws'

export default class Listener {
  /**
   * Create a new deployment listener
   * @param {Client} client - Scalingo API Client
   * @param {String} url - URL of the stream to listen to
   */
  constructor(client, url) {
    this._client = client
    this._messageTypes = {}
    this._url = url
    this._start()
  }

  _start() {
    this._ws = new WebSocket(this._url)
    this._ws.onopen = () => {
      this._auth()
    }

    this._ws.onclose = () => {
      if (this._onClose) {
        this._onClose()
      }
      this._ws = null
    }

    this._ws.onmessage = (message) => {
      this._onMessage(message)
    }
  }

  _auth() {
    this._ws.send(
      JSON.stringify({
        type: 'auth',
        data: {
          token: this._client._token,
        },
      }),
    )
  }

  _onMessage(message) {
    const data = JSON.parse(message.data)
    if (this._messageTypes[data.type]) {
      const result = data.data

      // If there was an ID in the original message
      if (data.id) {
        // Inject it in the result object
        result['id'] = data.id
      }
      this._messageTypes[data.type](result)
    }
  }

  /**
   * Close the listener connection
   */
  close() {
    if (this._ws) {
      this._ws.close()
      this._ws = null
    }
  }

  /**
   * Setup a callback that will be called when the connection is closed.
   * @param {function()} callback Callback to call when the connection is closed
   */

  onClose(callback) {
    this._onClose = callback
  }

  /**
   * Setup a callback that will be called when there is a new deployment.
   * @param {function(data: EventNewDeployment)} callback Callback to call when there is a new deployment
   */
  onNew(callback) {
    this._messageTypes['new'] = callback
  }

  /**
   * Setup a callback that will be called when a new log line is received.
   * @param {function(data: EventNewDeploymentLog)} callback Callback to call when a new log line is received
   */
  onLog(callback) {
    this._messageTypes['log'] = callback
  }

  /**
   * Setup a callback that will be called when the deployment status is updated.
   * @param {function(data: EventDeploymentStatusUpdated)} callback Callback to call when the deployment status is updated
   */
  onStatus(callback) {
    this._messageTypes['status'] = callback
  }
}

/**
 * @typedef {Object} EventNewDeployment
 * @see https://developers.scalingo.com/deployments#get-real-time-output-of-a-live-deployment
 * @property {String} deployment ID of the new deployment
 */

/**
 * @typedef {Object} EventNewDeploymentLog
 * @see https://developers.scalingo.com/deployments#get-real-time-output-of-a-live-deployment
 * @property {String} id Deployment ID
 * @property {String} content log line received
 */

/**
 * @typedef {Object} EventDeploymentStatusUpdated
 * @see https://developers.scalingo.com/deployments#get-real-time-output-of-a-live-deployment
 * @property {String} id Deployment ID
 * @property {DeploymentStatus} status New deployment status
 */

import { unpackData } from '../utils'

/**
 * Domains API Client
 */
export default class Domains {
  /**
   * Create a new Client for the Domains API
   * @param {Client} client - Scalingo API Client
   */
  constructor(client) {
    this._client = client
  }

