import axios from 'axios'
import { unpackData } from '../utils.js'

/**
 * Tokens API Client
 */
export default class Tokens {
  /**
   * Create a new Client for the Token API
   * @param {Client} client - Scalingo API Client
   */
  constructor(client) {
    this._client = client
  }

  /**
   * List all known tokens
   * @return {Promise<Token[] | APIError>} A list of tokens
   */
  all() {
    return unpackData(this._client.authApiClient().get('/tokens'), 'tokens')
  }

  /**
   * Create a new token
   * @param {String} name - The name of the new token
   * @return {Promise<Token | APIError>} The newly created token
   */
  create(name) {
    return unpackData(
      this._client.authApiClient().post('/tokens', { name }, null),
      'token',
    )
  }

  /**
   * Renew a token
   * @param {String} id - The id of the token to renew
   * @return {Promise<Token | APIError>} The newly created token
   */
  renew(id) {
    return unpackData(
      this._client.authApiClient().patch(`/tokens/${id}/renew`, null, null),
      'token',
    )
  }

  /**
   * Destroy a token
   * @param {String} id - The id of the token to destroy
   * @return {Promise<? | APIError>}
   */
  destroy(id) {
    return unpackData(this._client.authApiClient().delete(`/tokens/${id}`))
  }

  /**
   * Exchange a Token for a JWT
   * @param {!String} token A valid token
   * @return {Promise<String| APIError>} A valid Bearer Token that can be used against our infrastructure.
   */
  exchange(token) {
    const request = axios.post(
      `${this._client._authApiUrl}/v1/tokens/exchange`,
      {},
      {
        auth: {
          password: token,
        },
      },
    )
    return unpackData(request, 'token')
  }
}

/**
 * @typedef {Object} Token
 * @see https://developers.scalingo.com/tokens
 * @property {String} id ID of the token
 * @property {String} name Name of the token
 * @property {String} created_at Creation date of this token
 * @property {?String} last_used_at Date of the last usage of this token
 * @property {String} issued_at When was the token issued
 * @property {?String} token Actual token (only shown on token creation endpoint)
 */

import { unpackData } from '../utils.js'

export const TOTP_PROVIDER = 'totp'
export const DEFAULT_PROVIDER = TOTP_PROVIDER
export const SUPPORTED_PROVIDERS = [TOTP_PROVIDER]

export default class TwoFactorAuth {
  /**
   * Create a new Client for the Users API
   * @param {Client} client - Scalingo API Client
   */
  constructor(client) {
    this._client = client
  }

  /**
   * Returns the current user two-factor status
   * @return {Promise<TwoFactorAuthObject | APIError>} Promise resolving with the current user two factor status
   */
  status() {
    return unpackData(this._client.authApiClient().get('/client/tfa'), 'tfa')
  }

  /**
   * Initiate the two-factor activation process.
   * @param {?String} [provider=totp] the 2FA provider
   * @return {Promise<TwoFactorAuthInitiateResponse | APIError>} Promise resolving with the current user two factor status
   */
  initiate(provider) {
    const data = {
      tfa: { provider: provider || DEFAULT_PROVIDER },
    }

    return unpackData(
      this._client.authApiClient().post('/client/tfa', data),
      'tfa',
    )
  }

  /**
   * Validate the two-factor activation process.
   * @param {Number} attempt the "pin number" given by the authenticator
   * @return {Promise<TwoFactorAuthValidateResponse | APIError>} Promise resolving with the current user two factor status
   */
  validate(attempt) {
    const data = {
      tfa: { attempt },
    }

    return unpackData(
      this._client.authApiClient().post('/client/tfa/validate', data),
      'tfa',
    )
  }

  /**
   * Disable the two-factor auth for this user. Will raise an error if not enabled.
   * @return {Promise<TwoFactorAuthObject | APIError>} Promise resolving with the current user two factor status
   */
  disable() {
    return unpackData(this._client.authApiClient().delete('/client/tfa'))
  }
}

/**
 * @typedef {Object} TwoFactorAuthObject
 * @property {String} uuid
 * @property {Boolean} enabled
 * @property {?String} provider
 */

/**
 * @typedef {Object} TwoFactorAuthInitiateResponse
 * @property {String} uuid
 * @property {Boolean} enabled
 * @property {String} provider
 * @property {String} uri
 */

/**
 * @typedef {Object} TwoFactorAuthValidateResponse
 * @property {Array<String>} codes A list of recovery codes
 * @property {TwoFactorAuth} user The two factor auth status
 */

/**
 * Error returned when the Scalingo API return something else than 200
 */
export class APIError extends Error {
  /**
   * Create a new instance of APIError
   * @param {Number} status HTTP status code of the request
   * @param {Object} data Body of the HTTP response (parsed as JSON)
   */
  constructor(status, data) {
    let message = `An error occurred (status=${status})`
    if (data && data.error) {
      message = data.error
    }
    super(message)
    this._status = status
    this._data = data

    // Remove ourself from the stack trace
    if ('captureStackTrace' in Error) {
      Error.captureStackTrace(this, APIError)
    } else {
      this.stack = new Error(this._status + this._data).stack
    }
  }

  /**
   * HTTP Status code returned by the API
   * @return {Number}
   */
  get status() {
    return this._status
  }

  /**
   * Body of the HTTP response (parsed as JSON)
   * @return {Object}
   */
  get data() {
    return this._data
  }
}

